package com.edwin.common.tools.lang;/** * @author jinming.wu * @date 2014-4-8 */public class StringHelper {    /**     * 字符串拼接     *      * @param strs     * @return     */    public static String join(String... strs) {        StringBuffer buffer = new StringBuffer();        for (String str : strs) {            if (str != null) {                buffer.append(str);            }        }        return buffer.toString();    }}